import { Component } from '@angular/core';
import { Nhakhoahoc } from './nhakhoahoc';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'Bài Lab 1';
  student = {
    hoten: 'Nguyễn Ngọc Hải',
    phai: 0,
    ngaysinh: '2003-01-22',
    hinh: 'hinh1.jpg',
    diem: 7,
  };
  listNhaKH: Nhakhoahoc[] = [
    { id: 1, ho: 'Tạ Quang', ten: 'Bửu', sinh: 1910, mat: 1986 },
    { id: 2, ho: 'Nguyễn', ten: 'Văn A', sinh: 1995, mat: 2020 },
    { id: 3, ho: 'Trần', ten: 'Thị B', sinh: 1988, mat: 2010 },
    { id: 4, ho: 'Lê', ten: 'Đình C', sinh: 2000, mat: 2022 },
    { id: 5, ho: 'Phạm', ten: 'Văn D', sinh: 1992, mat: 2015 },
  ];
  show(codehtml: string) {
    var kq = document.querySelector('#ds') as HTMLElement;
    kq.innerHTML = codehtml;
  }
  render() {
    let codehtml = this.listNhaKH
      .map((nkh) => {
        return `
        <p>${nkh.id}. ${nkh.ho} ${nkh.ten} ( ${nkh.sinh} - ${nkh.mat} )</p>
      `;
      })
      .join('');
    this.show(codehtml);
  }
}
<div class="container">
  <div class="card text-center">
    <div class="card-header">THÔNG TIN SINH VIÊN</div>
    <div class="card-body">
      <ul class="list-group">
        <li class="list-group-item">Họ tên: {{student.hoten}}</li>
        <li class="list-group-item">Ngày sinh: {{student.ngaysinh}}</li>
        <li class="list-group-item">Phái: {{student.phai}}</li>
        <li class="list-group-item">Điểm: {{student.diem}}</li>
        <li class="list-group-item"><img style="width: 200px; height: 200px; background-size: cover;" src="assets/{{student.hinh}}" alt=""></li>
      </ul>
    </div>
    <div class="card-footer text-muted">Lab 1 môn Angular</div>
  </div>
</div>
